document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("google-reviews-badge"),t=document.getElementById("google-reviews-sidebar"),n=document.querySelector(".google-reviews-sidebar__close");e&&t&&n&&(e.addEventListener("click",function(){t.classList.add("open"),document.querySelectorAll(".google-review").forEach((e,t)=>{e.style.animation="none",e.offsetHeight,e.style.animation=`slideIn 0.6s ease ${.5*t}s forwards`})}),n.addEventListener("click",function(){t.classList.remove("open"),document.querySelectorAll(".google-review").forEach(e=>{e.style.animation="none"})}),window.addEventListener("scroll",function(){const t=window.scrollY,n=window.innerHeight,o=document.documentElement.scrollHeight;t+n>=o-100?(e.style.transform="translateY(100px)",e.style.opacity="0.3"):(e.style.transform="translateY(0)",e.style.opacity="1")}),document.addEventListener("click",function(n){window.innerWidth<768&&t.classList.contains("open")&&!t.contains(n.target)&&!e.contains(n.target)&&t.classList.remove("open")})),document.querySelectorAll(".read-more").forEach(function(e){e.addEventListener("click",function(e){e.preventDefault();const t=this.closest(".google-review__body");t.querySelector(".short-text").style.display="none",t.querySelector(".full-text").style.display="inline",this.style.display="none"})})});